/**
 * @param {number} numerator
 * @param {number} denominator
 * @return {string}
 */
var fractionToDecimal = function(n, d) {
    if (n === 0) {
        return "0";
    }
    const sign = n > 0 && d > 0 || n < 0 && d < 0 ? "" : "-";
    n = Math.abs(n);
    d = Math.abs(d);
    let r = String(Math.trunc(n / d));
    let rem = n % d;
    if (rem === 0) {
        return sign + r;
    }

    r += ".";
    const map = new Map();
    map.set(rem, r.length);
    while (rem > 0) {
        rem *= 10;
        r += String(Math.trunc(rem/d));
        rem = rem % d;
        const ex = map.get(rem);
        if (ex !== undefined) {
            return sign + r.substring(0, ex) + "(" + r.substring(ex) + ")";
        }
        map.set(rem, r.length);
    }

    return sign + r;
};
