class Solution {
    public String fractionToDecimal(int numerator, int denominator) {
        if (numerator == 0) return "0";
        StringBuilder sb = new StringBuilder();
        String sign = (numerator < 0 && denominator < 0 || numerator >= 0 && denominator >= 0) ? "" : "-";
        long n = Math.abs(Long.valueOf(numerator));
        long d = Math.abs(Long.valueOf(denominator));
        sb.append(sign);
        // integral part
        sb.append(n / d);
        if (n % d == 0) return sb.toString();
        sb.append(".");
        // fractional part
        // keeps track of seen numerators and the length of sb
        Map<Long, Integer> m = new HashMap<>();
        while (n % d != 0) {
            n %= d;
            if (m.containsKey(n)) {
                // found a cycle
                sb.insert(m.get(n), "(");
                sb.append(")");
                return sb.toString();
            }
            m.put(n, sb.length());
            n *= 10;
            sb.append(n / d);
        }
        return sb.toString();
    }
}
