class Solution {

    void ComputeAfterDecimal(long num , long den , string &res){
        unordered_map<long , long>history;
        long q , r;

        while(num){
            q = num/den;
            r = num%den;

            //i have seen q for first time
            if(history.find(num) == history.end()){
                history[num] = res.size();
                res+=to_string(q);
                num =r*10;
            }
            //found a recurring event
            else{
                //insert brackets
                res = res.substr(0 , history[num]) + "(" + res.substr(history[num] ,res.size() -history[num] ) + ")";
                break;
            }
        }
    }



public:
    string fractionToDecimal(int numerator, int denominator) {
        if(numerator == 0) return "0";

        string res = "";
        //for negative numbers division
        if(( numerator >0 && denominator < 0) || (numerator <0 && denominator > 0) )
            res +="-";

        //absolute divison
        long num = (long)abs(numerator);
        long den = (long)abs(denominator);

        //part before decimal point
        long q = num/den;
        long rem = num % den;

        res+=to_string(q);

        if(rem == 0)
            return res;

        //non-zero remainder which means i need a decimal point
        res+=".";

        ComputeAfterDecimal(rem*10 , den , res);

        return res; 
    }
};
