class Solution {
public:
    #define ll long long int
    string fractionToDecimal(int num, int den) {
        string res="";
        bool isNeg=false;
        if((num>0&&den<0)||(num<0&&den>0)){
            isNeg=true;
        }
        ll n=(ll)abs(num);
        ll d=(ll)abs(den);
        if(n%d==0){
            res=to_string(n/d);
        }
        else{
            ll rem=n%d;
            res=to_string(n/d);
            res+=".";
            string temp="";
            unordered_map<ll,string> mp;
            mp[rem]="";
            while(rem!=0){
                rem=rem*10;
                if(rem<d){
                    temp+="0";
                }
                else{
                    temp+=to_string(rem/d);
                    rem=rem%d;
                    if(mp.find(rem)!=mp.end()){
                        temp=mp[rem]+"("+ temp.substr(mp[rem].size()) +")";
                        break;
                    }
                    else{
                        mp[rem]=temp;
                    }
                }
            }

            res=res+temp;

        }


        if(isNeg) return "-"+res;
        return res;
    }
};
