string fractionToDecimal(int numerator, int denominator) {
    long long int num = numerator;
    long long int den = denominator;
    if(num == 0)
        return "0";
    string res;
    int sign = 1;
    sign *= num > 0?1:-1;
    sign *= den >= 0 ? 1: -1;
    num = abs(num);
    den = abs(den);
    long long int quotient = num / den;
    num = num % den;
    if(num == 0)
        res += to_string(quotient);
    else
        res += to_string(quotient) + ".";
    int every;
    vector<int> m_v;
    int x = res.size();
    int st = 0;
    while(num!=0){
        m_v.push_back(num);
        num *= 10;
        every = num / den;
        res += to_string(every);
        num %= den;
        auto it = std::find(m_v.begin(), m_v.end(), num);
        if(it != m_v.end()){
            st = it - m_v.begin() + x;
            res = res.substr(0, st) + "(" + res.substr(st) + ")";
            break;
        }
    }
    if(sign == -1)
        res = "-" + res;
    return res;
}
