typedef long long ll;
class Solution {
public:
    string fractionToDecimal(ll nume, ll deno) {
        if(nume==0) return "0";
        string ans="";
        if((nume<0 or deno<0)and not(nume<0 and deno<0))
            ans+="-";
        nume=abs(nume);
        deno=abs(deno);

        ans+=to_string(nume/deno);
        ll rem=nume%deno;
        if(rem==0){
            return ans;
        }
        //dealing with after float value
        ans+=".";
        map<ll,ll> mm;
        while(rem){
            if(mm.find(rem)!=mm.end()){
                auto it=mm.find(rem);
                int pos=it->second;
                string str1=ans.substr(0,pos);
                string str2=ans.substr(pos);
                ans=str1+"("+str2+")";
                break;
            }
            else{
                mm.insert(make_pair(rem,ans.length()));
                rem*=10;
                int q=rem/deno;
                ans+=to_string(q);
                rem%=deno;
            }
        }
        return ans;
    }
};
```

