            records[0][i] = index2;
            records[1][i] = count;
        }

        if (cycleStart == -1) {
            return count / n2;
        }

        int res = 0;
        // calculate cycle
        int cycleN = (n1 - cycleStart) / (cycleEnd - cycleStart);
        res = cycleN * cycleCount;
        // snitich pre-cycle and post-cycle parts
