    def getMaxRepetitions(self, s1: str, n1: int, s2: str, n2: int) -> int:
        if n1 == 0:
            return 0

        # Count the number of occurrences of s2 in each repetition of s1
        repeat_count = {}  # Map of count2: (count1, next_index)
        count1, count2 = 0, 0
        next_index = 0

        while count1 < n1:
            for i in range(len(s1)):
                if s1[i] == s2[next_index]:
                    next_index += 1
                    if next_index == len(s2):
                        next_index = 0
                        count2 += 1

            count1 += 1

            if next_index in repeat_count:
                # Found a repeating pattern
                prev_count1, prev_count2 = repeat_count[next_index]
                pattern_count1 = count1 - prev_count1
                pattern_count2 = count2 - prev_count2
                pattern_repetitions = (n1 - prev_count1) // pattern_count1
                remaining_count1 = (n1 - prev_count1) % pattern_count1

                count2 = prev_count2 + pattern_count2 * pattern_repetitions
                count1 = prev_count1 + pattern_count1 * pattern_repetitions

                # Calculate remaining occurrences within the partial pattern
                for _ in range(remaining_count1):
                    for char in s1:
                        if char == s2[next_index]:
                            next_index += 1
                            if next_index == len(s2):
                                next_index = 0
                                count2 += 1

                break
            else:
                repeat_count[next_index] = (count1, count2)

        return count2 // n2

