        if(s2r == s1r) 
        {   if(b == 0) { if(e[a] == -1) { e[a] = d; g[a] = c; } else f = a; } 
            if(!(s2r = s2[++b])) { s2r = s2[(b = 0)]; d++; }
        }
    }
    return (d / n2);
earlycalc: ;
//  counting continues after loop is found to end of current s1 cycle. 
//  s2 count is taken at the end of each s1 cycle

