class Solution {
    bool alls2Ins1(string s2,string s1){
       map<char,bool>mp;
        for(auto e:s1)
            mp[e]=true;
        for(auto e:s2){
            if(!mp[e])
                return false;
        }
        return true;
    }
public:
    int getMaxRepetitions(string s1, int n1, string s2, int n2) {
        int n=s1.size();
        int m=s2.size();

        // find how many times can s2 be made a subsequence of [s1,n1]

        if(!alls2Ins1(s2,s1))
            return 0;

            int maxMatch[m];
        memset(maxMatch,0,sizeof(maxMatch));

        for(int i=0;i<m;i++){
            int ptr=i;

            for(int j=0;j<n;j++){
                if(s1[j]==s2[ptr]){
                  maxMatch[i]++;
                    ptr++;
                    ptr%=m;
                }
            }

        }

        int tot=0;

        int cur=0;

        for(int i=0;i<n1;i++){
           int matched= maxMatch[cur];
            tot+=matched;
            cur+=matched;
            cur%=m;
        }

        return (tot/m)/n2;
    }
};
