class Solution {
public:
    int getMaxRepetitions(string s1, int n1, string s2, int n2) {
        int i = 0;
        int j = 0;
        int sn2 = n2;
        int sn1 = n1;
        unordered_map<long long,long long> m;
        bool found = false;
        long long  ii = 0;
        long long  jj = 0;
        while(n1>0)
        {
            if (s1[i]==s2[j]) 
            {
                if (m.count(i*1000000+j))
                {
                    int ii1 = m[i*1000000+j]/1000000;
                    int jj1 = m[i*1000000+j]%1000000;
                    int ii_diff = ii-ii1;
                    int jj_diff = jj-jj1;
                    int  x = (s1.size()*sn1-ii)/ii_diff;
                    ii += x*ii_diff;
                    jj += x*jj_diff;
                    i = ii%s1.size();
                    j = jj%s2.size();
                    n1 = sn1 -ii/s1.size();
                    n2 = sn2 -jj/s2.size();
