	}

	for(int i=1;i<N;i++){
		for(int j=0;j<s2.size();j++){
			dp[i][j] = dp[i-1][j] + dp[i-1][(j+dp[i-1][j])%s2.size()];
		}
	}

	int k = 0;
	int offset = 0;
	for(int i=0;i<N;i++){
		if(n1%2 == 1) {
			int m = dp[i][offset];
			k += m;
			offset = (offset+m)%s2.size();
