class Solution {
public:
    static int getMaxRepetitions(string const& s1, int n1, string const& s2, int n2) {
        int const s1_len = s1.size();
        int const s2_len = s2.size();
        int s2_reps = 0;
        int s2_idx = 0;
        // "match" will store (s1_idx, (s1_reps, s2_reps)) whenever s1[s1_idx] matches s2[s2_len - 1]
        unordered_map<int, pair<int, int>> match;
        match.reserve(s1_len);
        for (int s1_reps = 0; s1_reps < n1; ++s1_reps) {
            bool has_match = false;
			// iterate over a fresh copy of s1
            for (int s1_idx = 0; s1_idx < s1_len; ++s1_idx) {
                if (s1[s1_idx] == s2[s2_idx]) {
                    has_match = true;
                    if (++s2_idx == s2_len) {
                        ++s2_reps;
                        s2_idx = 0;
                        auto iter = match.find(s1_idx);
                        if (iter != match.end()) {
                            auto const prev_s1_reps = iter->second.first;
                            auto const prev_s2_reps = iter->second.second;
                            // preform a linear extrapolation using the current (s1_reps, s2_reps)
                            // and the previous (s1_reps, s2_reps) to skip over a large number of
							// intermediate iterations
                            auto const n_iters = (n1 - 1 - s1_reps) / (s1_reps - prev_s1_reps);
                            s1_reps += n_iters * (s1_reps - prev_s1_reps);
                            s2_reps += n_iters * (s2_reps - prev_s2_reps);
                        } else {
                            match[s1_idx] = make_pair(s1_reps, s2_reps);
                        }
                    }
                }
            }
            if (!has_match)
                // If we did not make progress at all with the current copy of s1, then
				// the char at s2[s2_idx] must be missing in s1. Therefore a full match is
				// impossible.
                return 0;
        }
        return s2_reps / n2;
    }
};
