class Solution {
public:
    int getMaxRepetitions(string s1, int n1, string s2, int n2) {
        int l1 = s1.size();
        int l2 = s2.size();
        int p1 = 0; // pointer for s1, in range [0, n1*l1]
        int p2 = 0; // pointer for s2 in range [0, n2*l2*M]

        int o2p1[101][101]; // p1 value when offset on s1 & s2 is given
        int o2p2[101][101]; // p2 value when offset on s1 & s2 is given

        for (int i = 0; i <= 100; i++)
            for (int j = 0; j <= 100; j++){
                o2p1[i][j] = -1;
                o2p2[i][j] = -1;
            }

        while (p1 < l1 * n1){
            if (s1[p1 % l1] == s2[p2 % l2]){
                int off1 = p1 % l1; // p1's offset across s1
                int off2 = p2 % l2; // p2's offset across s2
                int prev1 = p1; // store p1 value before update
                int prev2 = p2; // store p2 value before update
                if (o2p1[off1][off2] != -1 && o2p2[off1][off2] != -1){ // repetition found
                    int times = (l1 * n1 - p1) / (p1 - o2p1[off1][off2]); // possible remaining repititions
                    p1 += times * (p1 - o2p1[off1][off2]); // update p1 by repititions
                    p2 += times * (p2 - o2p2[off1][off2]); // update p2 by repititions
                }
                else { // no repetition found
                    o2p1[off1][off2] = p1; // log down p1
                    o2p2[off1][off2] = p2; // log down p2
                }
                if (prev1 == p1 && prev2 == p2){ // no pointer update
                    p1 ++;
                    p2 ++;
                }
            }
            else
                p1 ++;
        }
        return (p2 / l2) / n2;
    }
};
