class Solution(object):
    def getMaxRepetitions(self, s1, n1, s2, n2):
        """
        :type s1: str
        :type n1: int
        :type s2: str
        :type n2: int
        :rtype: int
        """
        idx2 = 0
        nextIdx = [0]
        rounds2 = [0]
        repeats2 = 0

        for rounds in range(min(len(s2)+1, n1)):
            count = 0
            for idx1 in range(len(s1)):
                if s1[idx1] == s2[idx2]:
                    idx2 += 1
                    count += 1
                    if idx2 == len(s2):
                        repeats2 += 1
                        idx2 = 0
            for i in range(len(nextIdx)):
                if nextIdx[i] == idx2:
                    return (rounds2[i] + ((n1-i) / (rounds + 1 - i))*(repeats2 - rounds2[i]) + rounds2[(n1-i) % (rounds + 1 - i)]) / n2
            nextIdx.append(idx2)
            rounds2.append(repeats2)
            if count == 0:
                return(0)
        return(rounds2[n1]/n2)

