class Solution {
public:
    int getMaxRepetitions(string s1, int n1, string s2, int n2) {
        int idx=0;
        int len1=s1.length();
        int len2=s2.length();
        vector<vector<int> > dps1(len1, vector<int>(len2, -1)), dps2(len1, vector<int>(len2, -1));
        for(int i=0;i<n1*len1;++i){
            if(s2[idx%len2]==s1[i%len1]){
                if(dps1[i%len1][idx%len2]!=-1 && i+i-dps2[i%len1][idx%len2]<n1*len1){
                    idx+=idx-dps1[i%len1][idx%len2];
                    i+=i-dps2[i%len1][idx%len2]-1;
                }
                else{
                    dps1[i%len1][idx%len2]=idx;
                    dps2[i%len1][idx%len2]=i;
                    ++idx;                    
                }
            }
        }
        return idx/len2/n2;
    }

};
