public class Solution {
    class MatchIndexLoop{
        int countLoop;
        int extraStep;
        MatchIndexLoop(int count_loop,int extra_step){
            countLoop=count_loop;
            extraStep=extra_step;
        }
    }
    private int count=0;
    public int getMaxRepetitions(String s1, int n1, String s2, int n2) {
        if(originalCheck(s1,s2)==false){
            return 0;
        }

        int i=0;
        Map<Integer,MatchIndexLoop> map = new HashMap<>();

        while(i<n1*s1.length()){
             int index=i%s1.length();
             if(map.containsKey(index)){
                 count++;
                 MatchIndexLoop t=map.get(index);
                 i+=t.countLoop*s1.length()+t.extraStep;
             }else{
                 MatchIndexLoop t = findNextPos(map,s1,index,s2);
                 i+=t.countLoop*s1.length()+t.extraStep;
                 count++;
             }

            if(i>=n1*s1.length()){
                count--;
                break;
            }
            i++;

        }

        return count/n2; 
    }

    public MatchIndexLoop findNextPos(Map<Integer,MatchIndexLoop> map,String str,int start_index,String target){
           int loop=0;
           String str2=str.substring(start_index)+str.substring(0,start_index);

           char[] strarray=str2.toCharArray();
           char[] target_array=target.toCharArray();

           int p=0;
           int i=0;
           for(;i<strarray.length;i++){
               if(target_array[p]==strarray[i]){
                   p++;
               }
               if(p==target_array.length){
                   break;
               }
               if(i+1==strarray.length){
                   loop++;
                   i=-1;
               }
           }
           MatchIndexLoop res = new MatchIndexLoop(loop,i);
           map.put(start_index,res);

           return  res;
    }

    public boolean originalCheck(String s1,String s2){
        char[] array1=s1.toCharArray();
        char[] array2=s2.toCharArray();

        boolean[] hash = new boolean[256];
        for(int i=0;i<array1.length;i++){
            hash[array1[i]]=true;
        }

        for(int i=0;i<array2.length;i++){
            if(hash[array2[i]]==false){
                return false;
            }
        }
        return true;
    }
}

