        int n=mat[0].size();
        if(m*n!=r*c)
            return mat;
        vector<vector<int>> res(r);
        int ri=0,ci=0;
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(ci==c)
                {
                    ri=ri+1;
                    ci=0;
                }
                res[ri].push_back(mat[i][j]);
