class Solution {
public:
    vector<vector<int>> matrixReshape(vector<vector<int>>& mat, int r, int c) {
        int r0 = mat.size(), c0 = mat[0].size();
        if (r0*c0 != r*c) return mat;

        vector<int> v0(c);
        vector<vector<int>> v(r, v0);

        // this is a approach to reduce the no of lines of code
