var matrixReshape = function(mat, r, c) {
    const result = [];

    // Detect if the matrices can't map
    if (r * c !== mat.length * mat[0].length) return mat;

    // Create pointers to track the original matrix
    let pR = 0;
    let pC = 0;

    for (let row = 0; row < r; row++) {
        // Build a row to store the columns
        const target = [];
        result.push(target);

        for (let col = 0; col < c; col++) {
            // Push the new column
            target.push(mat[pR][pC]);

            // Update the pointer to look at the next matrix item
            pC++;

            // If we've hit undefined we're overflowing, move to the next row and reset the columns
            if (mat[pR][pC] === undefined) {
                pR++;
                pC = 0;
            }
        }
    }

    return result;
};
```

