class Solution {
public:
    vector<vector<int>> matrixReshape(vector<vector<int>>& nums, int r, int c) {
        int rows = nums.size();
        int cols = nums[0].size();
        if(rows * cols != r * c || ( rows == r && cols == c)){
            return nums;
        }
        vector<vector<int>> result(r,vector<int>(c,0));
        int current = 0;
        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                int x = current / cols;
                int y = current % cols;
                result[i][j] = nums[x][y];
                current ++;
            }
        }
        return result;
    }
};
