func matrixReshape(mat [][]int, r int, c int) [][]int {
    n,m := len(mat), len(mat[0])

    if n * m != r * c {
        return mat
    }

    ans := make([][]int, r)
    for i := range ans {
        ans[i] = make([]int,c)
    }

    ch,done := make(chan int),make(chan struct{})
    go receiveVal(&mat,n,m,ch)
    go sendVal(&ans,r,c,ch,done)
    <- done
    return ans
}

func receiveVal(mat *[][]int,n,m int,ch chan<- int) {
    for i := 0; i < n; i++ {
        for j := 0; j < m; j++ {
            ch <- (*mat)[i][j]
        }
    }
}

func sendVal(ans *[][]int,n,m int,ch <-chan int,done chan struct{}) {
    for i := 0; i < n; i++ {
        for j := 0; j < m; j++ {
            (*ans)[i][j] = <- ch
        }
    }
    done <- struct{}{}
}
