class Solution {
public:
    vector<vector<int>> matrixReshape(vector<vector<int>>& mat, int r, int c) {
        int m=mat.size(),n=mat[0].size();
        vector<vector<int>> ans(r,vector<int> (c));
		// if all elements doesn't fit into another vector 
        if(m*n!=r*c){
            return mat;
        }
		// (i/c) gives new row number to insert.
		// (i%c) gives new column number to insert.
        for(int i=0;i<m*n;i++){
            ans[i/c][i%c]=mat[i/n][i%n];
        }
        return ans;

		------- Method 2 ------
            int row=0,col=0;
			if(m*n!=r*c){
				return mat;
			}
			// traversing over all vectors
			for(int i=0;i<m;i++){
				// traversing over vectors inner elements
				 for(int j=0;j<n;j++){
					 // putting inside elements
					 ans[row][col]=mat[i][j];
					 // after one element insertion increment col to insert next element in same row.
					 col++;
					 // if that row fully got filled, then increment row to fillin next row 
					 if(col==c){
						 row++;
						 // to start filling from first
						 col=0;
					 }
				 }
			}
			return ans;

    }
};
