impl Solution {
    pub fn matrix_reshape(mat: Vec<Vec<i32>>, r: i32, c: i32) -> Vec<Vec<i32>> {
        let m = mat.len();
        let n = mat[0].len();

        if m * n != (r * c) as usize {
            return mat;
        }

        let mut result = vec![vec![0; c as usize]; r as usize];
        let mut i = 0;
        let mut j = 0;

        for row in mat {
            for num in row {
                result[i][j] = num;
                j += 1;

                if j == c as usize {
                    j = 0;
                    i += 1;
                }
            }
        }

        result
    }
}
```

