public int[] constructArray(int n, int k) {
    int[] A = new int[n];
    for(int i = k; i < n; i++) A[i] = i + 1;
    for(int i = k - 1, val = 1; i >= 0; i -= 2) A[i] = val++;
    for(int i = k - 2, val = k; i >= 0; i -= 2) A[i] = val--;
    return A;
}
