class Solution {
public:
    vector<int> constructArray(int n, int k) {
        deque<int> deq (n);
        iota(deq.begin(), deq.end(), 1);
        k -= 1;
        bool f = true;
        vector<int> ans;
        while (!deq.empty()) {
            if (f) {
                ans.emplace_back(deq.front());
                deq.pop_front();
            } else {
                ans.emplace_back(deq.back());
                deq.pop_back();
            }
            if (k) {
                f = !f;
                k-=1;
            }
        }
        return ans;
    }
};
