object Solution {
    def constructArray(n: Int, k: Int): Array[Int] = {
      var num = -1
      val res = new Array[Int](n)
      res(0) = n
      for (i <- 1 until n) 
        if (i < k)
          if (num > 0) {
            res(i) = res(i - 1) + (n - i)
            num *= -1
          } else {
            res(i) = res(i - 1) - (n - i)
            num *= -1
          }
        else res(i) = res(i - 1) + num
      res
    }
}
```

