class Solution {
public:
    vector<int> constructArray(int n, int k) {
        vector<int> ans;
        vector<int> hash(n+10, 0);
        ans.push_back(1);
        hash[1] = 1;
        int num = 1;
        while(k){
            int t1 = num - k;
            if(t1 > 1 && hash[t1] == 0){
                hash[t1] = 1;
                ans.push_back(t1);
                num = t1;
                k--;
            }
            else{
                int t2 = num + k;
                hash[t2] = 1;
                ans.push_back(t2);
                num = t2;
                k--;
            }
        }
        for(int i = 1; i<=n; i++){
            if(hash[i] == 0){
                ans.push_back(i);
            }
        }
        return ans;
    }
};
```

