            if(curr - i > 0 and track[curr - i] == 0) curr = curr - i;
            else curr = curr + i;

            ans.push_back(curr);
            track[curr] = 1;
        }

        for(int i=1; i<=n; i++)
            if(track[i] == 0) ans.push_back(i);

        return ans;    
    }
};
