class Solution {
public:
    vector<int> constructArray(int n, int k) {
        vector<int> finalAns;
        int start = 1;
        int end = n;
        int ind =0;
        while(ind<n && start<end){
            if(ind&1)
            finalAns.push_back(end--); 
            else
            finalAns.push_back(start++);
            k--;
            if(k==0){
                break;
            }
            ind++;
        }

        if(ind&1){
            ind++;
            for(int i=end;i>=start;i--)
              finalAns.push_back(i);
        }else{
            for(int i=start;i<=end;i++)
              finalAns.push_back(i);
        }

        return finalAns;
    }
};
