class Solution {
public:
    vector<int> constructArray(int n, int k) {   
        vector<int> ans;
        int num = n, diff = k, lowest = n;
        ans.push_back(num);
        for(int i=1;i<=k;i++)
        {
           if(i&1){ans.push_back(num-diff);num-=(diff--);lowest=min(lowest, num);} 
           else{ans.push_back(num+diff);num+=(diff--);}
        }
        for(int i=lowest-1;i>0;i--)ans.push_back(i);
        return ans;
    }
};
