class Solution:
    def constructArray(self, n: int, k: int) -> List[int]:
        map = {}
        for i in range(1, n+1):
            map[i] = 1

        ans = []
        last = 1
        map[last] = 0
        ans.append(last)
        while k > 1:
            next = last - k if (last - k > 0 and map[last-k] == 1) else last + k
            last = next
            map[last] = 0
            ans.append(last)
            k -= 1
        for i in range(1, n+1):
            if map[i] == 1:
                ans.append(i)
        return ans

