        while(i<j){
            ans[index++] = i++ ;
            ans[index++] = j-- ;
        }
        if(i==j)ans[index++] = i ;

        // filling rem num in ans
        k+=2 ;
        while(k<=n){
            ans[index++] = k++ ;
        }
        return ans ;

