```c++
class Solution {
public:
    /*
        1, 2, 3, ..., n - 1, n         k = 1

        1, n, 2, n - 1, 3, n - 2, ...  k = n - 1

        construct sequence with same diff with first pattern using n - k numbers

        construct sequence with k - 1 diff with the second pattern using last m numbers (m = k)

    */
    vector<int> constructArray(int n, int k) {
        vector<int> res;
        for (int i = 0; i < n - k - 1; i++) {
            res.push_back(i + 1);
        }

        for (int t = n - k - 1, i = n - k, j = n; t < n;) {
            res.push_back(i);
            i++, t++;
            if (t < n) {
                res.push_back(j);
                j--, t++;
            }
        }

        return res;
    }
};
```

