        for (int i = 0; i < n - k - 1; i++) {
            res.push_back(i + 1);
        }

        for (int t = n - k - 1, i = n - k, j = n; t < n;) {
            res.push_back(i);
            i++, t++;
            if (t < n) {
                res.push_back(j);
                j--, t++;
            }
        }

        return res;
    }
};
