/**
 * 1. let n = 5, k = 1
 *    1, 2, 3, 4, 5
 * 2. let n = 5, k = 2
 *    1, 3, 2, 4, 5
 * 3. let n = 5, k = 3
 *    1, 4, 2, 3, 5
 * 4. let n = 5, k = 4
 *    1, 5, 2, 4, 3
 *
 * for any `n` and `k`,
 * 1, k + 1, 2, k, 3, k - 2, ..., k + 2, k + 3, ..., n
 *
 * Time Complexity: O(n)
 * Space Complexity: O(1)
 */
class Solution {
 public:
  vector<int> constructArray(const int n, const int k) {
    vector<int> ret(n);
    int index = 0;
    int begin = 1;
    int end = k + 1;
    while (begin <= end) {
      ret[index++] = begin;
      if (begin != end) {
        ret[index++] = end;
      }
      ++begin;
      --end;
    }
    for (int i = k + 2; i < n + 1; ++i) {
      ret[i - 1] = i;
    }
    return ret;
  }
};
```

