var constructArray = function(n, k) {
    const ans = [1];

    const used = new Map();
    used.set(1, true);

    for(let i = n - 1; i > 0; i--) {
        if(i <= k) {
            const plus = ans.at(-1) + i, minus = ans.at(-1) - i;
            if(!used.get(plus) && plus <= n && plus >= 1) 
                ans.push(plus), used.set(plus, true);
            else 
                ans.push(minus), used.set(minus, true);
        } else {
            ans.push(ans.at(-1) + 1);
        }
    }
    return ans;
};
