object Solution {
  def constructArray(n: Int, k: Int): Array[Int] = {
    def constructArrayImpl(n: Int, k: Int, delta:Int, acc:List[Int]): Array[Int] = {
      lazy val newDelta = if(delta>0) 1-delta else -delta-1
      lazy val deltaSign = if(delta>0) 1 else -1
      if(n==0) acc.toArray.reverse
      else if(k==0) 
        acc.reverse.to(Array) ++
          ((acc.head + deltaSign) to (acc.head + n*(deltaSign)) by deltaSign).to(Array) 
      else 
        constructArrayImpl(n-1, k-1, newDelta, (acc.head + delta) +: acc)
    }
    constructArrayImpl(n-1, k-1, n-1, List(1))
  }
}
