class Solution {
    func constructArray(_ n: Int, _ k: Int) -> [Int] {
        var queue: [Int] = []
        for i in 1..<(n + 1) {
            queue.append(i)
        }

        var result: [Int] = []
        var lower: Int = 0
        var upper: Int = k
        for i in 1...(k + 1) {
            if lower > upper {
                break
            }
            if i % 2 == 0 {
                result.append(queue[upper])
                upper -= 1
            } else {
                result.append(queue[lower])
                lower += 1
            }
        }

        for i in (k + 1)..<queue.count {
            result.append(queue[i])
        }

        return result
    }
}
