class Solution:
    def constructArray(self, n: int, k: int) -> List[int]:
        l,h = 1,n
        res = [l]
        l += 1
        while k > 1:
            res.append(h)
            h -= 1
            k -= 1
            if k > 1:
                res.append(l)
                l += 1
                k -= 1
        if res[-1] == h+1:
            res += list(range(h,l-1,-1))
        else:
            res += list(range(l,h+1))
        return res
