            auto top1= pq.top();
            pq.pop();
            auto top2 = pq.top();
            pq.pop();

            res+=top1.second;
            res+=top2.second;

            top1.first -=1;
            top2.first -= 1;

            if(top1.first > 0)
                pq.push(top1);

            if(top2.first > 0)
                pq.push(top2);
        }

        if(!pq.empty()){
            if(pq.top().first > 1)
                return "";

            else
                res+=pq.top().second;
        }

        return res;
    }
};
