```C# []
using System;
using System.Collections.Generic;

public class Solution {
    public string ReorganizeString(string s) {
        Dictionary<char, int> count = new Dictionary<char, int>();
        foreach (char c in s) {
            if (count.ContainsKey(c)) {
                count[c]++;
            } else {
                count[c] = 1;
            }
        }

        List<int[]> maxHeap = new List<int[]>();
        foreach (var entry in count) {
            maxHeap.Add(new int[] {-entry.Value, entry.Key});
        }
        Heapify(maxHeap);

        int[] prev = null;
        string res = "";
        while (maxHeap.Count > 0 || prev != null) {
            if (prev != null && maxHeap.Count == 0) {
                return "";
            }

            int[] top = HeapPop(maxHeap);
            res += (char)top[1];
            top[0]++;

            if (prev != null) {
                HeapPush(maxHeap, prev);
                prev = null;
            }

            if (top[0] != 0) {
                prev = top;
            }
        }

        return res;
    }

    private void Heapify(List<int[]> heap) {
        int n = heap.Count;
        for (int i = n / 2 - 1; i >= 0; i--) {
            HeapifyDown(heap, i);
        }
    }

    private void HeapifyDown(List<int[]> heap, int index) {
        int n = heap.Count;
        int left = 2 * index + 1;
        int right = 2 * index + 2;
        int largest = index;

        if (left < n && heap[left][0] < heap[largest][0]) {
            largest = left;
        }
        if (right < n && heap[right][0] < heap[largest][0]) {
            largest = right;
        }

        if (largest != index) {
            Swap(heap, index, largest);
            HeapifyDown(heap, largest);
        }
    }

    private int[] HeapPop(List<int[]> heap) {
        int n = heap.Count;
        int[] top = heap[0];
        heap[0] = heap[n - 1];
        heap.RemoveAt(n - 1);
        HeapifyDown(heap, 0);
        return top;
    }

    private void HeapPush(List<int[]> heap, int[] element) {
        heap.Add(element);
        HeapifyUp(heap, heap.Count - 1);
    }

    private void HeapifyUp(List<int[]> heap, int index) {
        while (index > 0) {
            int parent = (index - 1) / 2;
            if (heap[index][0] >= heap[parent][0]) {
                break;
            }
            Swap(heap, index, parent);
            index = parent;
        }
    }

    private void Swap(List<int[]> heap, int i, int j) {
        int[] temp = heap[i];
        heap[i] = heap[j];
        heap[j] = temp;
    }
}
