```C# []
using System;
using System.Collections.Generic;

public class Solution {
    public string ReorganizeString(string s) {
        int[] f = new int[26];
        int n = s.Length;

        foreach (char ch in s) {
            f[ch - 'a']++;
            if (f[ch - 'a'] > (n + 1) / 2) {
                return "";
            }
        }

        var p = new PriorityQueue<(int freq, char ch)>();
        for (int i = 0; i < 26; i++) {
            if (f[i] != 0) {
                p.Enqueue((-f[i], (char)('a' + i)));
            }
        }

        var ansList = new List<char>();
        while (p.Count >= 2) {
            var (freq1, ch1) = p.Dequeue();
            var (freq2, ch2) = p.Dequeue();
            ansList.Add(ch1);
            ansList.Add(ch2);
            if (freq1 < -1) {
                p.Enqueue((freq1 + 1, ch1));
            }
            if (freq2 < -1) {
                p.Enqueue((freq2 + 1, ch2));
            }
        }

        if (p.Count == 1) {
            ansList.Add(p.Dequeue().ch);
        }

        return new string(ansList.ToArray());
    }

    public class PriorityQueue<T> where T : IComparable<T> {
        private readonly List<T> _heap = new List<T>();

        public int Count => _heap.Count;

        public void Enqueue(T item) {
            _heap.Add(item);
            int ci = _heap.Count - 1;
            while (ci > 0) {
                int pi = (ci - 1) / 2;
                if (_heap[ci].CompareTo(_heap[pi]) >= 0)
                    break;
                T tmp = _heap[ci]; _heap[ci] = _heap[pi]; _heap[pi] = tmp;
                ci = pi;
            }
        }

        public T Dequeue() {
            int li = _heap.Count - 1;
            T frontItem = _heap[0];
            _heap[0] = _heap[li];
            _heap.RemoveAt(li);

            --li;
            int pi = 0;
            while (true) {
                int ci = pi * 2 + 1;
                if (ci > li) break;
                int rc = ci + 1;
                if (rc <= li && _heap[rc].CompareTo(_heap[ci]) < 0)
                    ci = rc;
                if (_heap[pi].CompareTo(_heap[ci]) <= 0) break;
                T tmp = _heap[pi]; _heap[pi] = _heap[ci]; _heap[ci] = tmp;
                pi = ci;
            }
            return frontItem;
        }
    }
}

