class Solution {
public:
    string reorganizeString(string S) {
        priority_queue<pair<int, char>> pq;
        int map[26] = { 0 };

        for (auto c : S) {
            if (++map[c-'a'] > (S.size() + 1)/2)
                return "";
        }

        for (int i = 0; i < 26; ++i) {
            if (map[i])
                pq.push({map[i], i + 'a'});
        }

        string ans;
        while(!pq.empty()) {
            pair<int, char> p1, p2;
            p1 = pq.top(); pq.pop();
            ans.push_back(p1.second);
            if (!pq.empty()) {
                p2 = pq.top(); pq.pop();
                ans.push_back(p2.second);
                if (--p2.first)
                    pq.push(p2);
            }
            if (--p1.first)
                pq.push(p1);
        }
        return ans;
    }
};
