        if(maxFreq) return "";

        for(auto it: mp){
            char currChar=it.first;
            int currFreq=it.second;
            while(currFreq){
                ind=ind+2>=n?1: ind+2;
                s[ind]=currChar;
                currFreq--;
            }
        }

        return s;
    }
};
