public class Solution 
{
    public string ReorganizeString(string s) 
    {
        var charFreq = new Dictionary<char, int>();
        foreach(var ch in s)
        {
            if(!charFreq.ContainsKey(ch)) 
                charFreq[ch] = 0;
            charFreq[ch]++;
        }

        var list = charFreq.Select(x => new int[]{(int) x.Key, x.Value}).ToList();
        list.Sort((x, y) => -x[1].CompareTo(y[1]));

        if (list[0][1] > (s.Length + 1) / 2) 
            return ""; 

        var result = new char[s.Length];
        int index = 0;
        foreach(var elem in list) 
        {
            while (elem[1] > 0) 
            {
                if (index >= result.Length) 
                    index = 1;

                result[index] = (char) elem[0];
                index += 2;
                elem[1]--;
            }
        }

        return new String(result);
    }
}
