class Solution:
    def reorganizeString(self, S):
        char_count = Counter(S)
        max_heap = [(-count, char) for char, count in char_count.items()]
        heapq.heapify(max_heap)
        result = []
        prev_count, prev_char = 0, ""
        while max_heap:
            count, char = heapq.heappop(max_heap)
            result.append(char)
            if prev_count < 0:
                heapq.heappush(max_heap, (prev_count, prev_char))
            prev_count, prev_char = count + 1, char
        return "".join(result) if len(result) == len(S) else ""
```
```Java []

class Solution {
    public String reorganizeString(String S) {
        Map<Character, Integer> charCount = new HashMap<>();
        for (char c : S.toCharArray()) {
            charCount.put(c, charCount.getOrDefault(c, 0) + 1);
        }

        PriorityQueue<Character> maxHeap = new PriorityQueue<>((a, b) -> charCount.get(b) - charCount.get(a));
        maxHeap.addAll(charCount.keySet());

        StringBuilder result = new StringBuilder();
        char prevChar = '\0';

        while (!maxHeap.isEmpty()) {
            char currentChar = maxHeap.poll();
            result.append(currentChar);
            charCount.put(currentChar, charCount.get(currentChar) - 1);

            if (prevChar != '\0' && charCount.get(prevChar) > 0) {
                maxHeap.offer(prevChar);
            }

            prevChar = currentChar;
        }

        return result.length() == S.length() ? result.toString() : "";
    }
}

```
```C++ []

class Solution {
public:
    string reorganizeString(string S) {
        unordered_map<char, int> charCount;
        for (char c : S) {
            charCount[c]++;
        }

        auto comp = [](const pair<char, int>& a, const pair<char, int>& b) {
            return a.second < b.second;
        };

        priority_queue<pair<char, int>, vector<pair<char, int>>, decltype(comp)> maxHeap(comp);

        for (const auto& entry : charCount) {
            maxHeap.push(entry);
        }

        string result = "";
        char prevChar = '\0';

        while (!maxHeap.empty()) {
            auto current = maxHeap.top();
            maxHeap.pop();
            result += current.first;
            charCount[current.first]--;

            if (prevChar != '\0' && charCount[prevChar] > 0) {
                maxHeap.push({prevChar, charCount[prevChar]});
            }

            prevChar = current.first;
        }

        return result.length() == S.length() ? result : "";
    }
};

