            pq.pop();
            if (result.empty() || first[1] != result.back())
            //Char not matching with previously appended character
            {
                result += char(first[1]);
                if (--first[0] > 0)
                {
            //If the freq has not become 0 then again push it in the heap
                    pq.push(first);
