                if (pq.empty())
            //Koi char add karne ko hain hi nahin
                    return "";
                auto second = pq.top();
                pq.pop();
                result += char(second[1]);
                if (--second[0] > 0)
                    pq.push(second);
                pq.push(first);
            }
        }
        return result;
    }
