class Solution {
public:
    string reorganizeString(string S) {
        if(S.size()<2)
	{
		return S;
	}

	vector<int> counts(26, 0);
	int maxC = 1;
	int maxI = 0;
	for(auto ch : S)
	{
		counts[ch-'a']++;
		if(counts[ch-'a'] > maxC)
		{
			maxC = counts[ch-'a'];
			maxI = ch-'a';
		}
	}

	int n = S.size();
	if(maxC>(n+1)/2)
	{
		return "";
	}

	int i = 0;
	while(counts[maxI]--)
	{
		S[i] = maxI + 'a';
		i+=2; 
	}

	for(int j=0; j<26; j++)
	{
		while(counts[j]-->0)
		{
			if(i>=n)
			{
				i = 1;
			}

			S[i] = j+'a';
			i+=2;
		}
	}

	return S;
	}
};
